mapper.select:
    select distinct count(*)
    from (
    	select 
    		b1.*, bl.name as labelName 
    	from bill b1 left join billLabel bl on b1.labelId=bl.id 

      		and b1.labelId = ${labelId}


	      and (
	      	b.descs like '%${condition}%' 
	      	or bl.name like '%${condition}%' 
	      	or bl.descs like '%${condition}%'
	   	  )

    ) b, billSort bs 
    where 
      b.sortId = bs.id 
      and b.userId=${userId}

      and b.id=${billId}


      and b.type = ${type} 


      and b.sums  >=  ${minSum}


      and b.sums  <  ${maxSum}


      and b.dates  >= ' ${startTime} '


      and b.dates  < ' ${endTime} ' 


      and b.sortId = ${sortId}


      and b.labelId = ${labelId}


      and (
      	bs.name like '%${condition}%' 
      	or bs.descs like '%${condition}%' 
   	  )

 select distinct 
 	b.id as id, b.dates as dates,b.sums as sums, b.type as type, b.descs as descs, 
  	b.sortId, bs.name as sortName ,b.labelId as labelId, b.labelName as labelName 

  , b.createTime as createTime, b.updateTime as updateTime 

 from (
 	select 
 		b1.*, bl.name as labelName  
 	from bill b1 left join billLabel bl on b1.labelId=bl.id
 ) b , billSort bs 
 where 
   b.sortId = bs.id 
   and b.userId=${userId}

   and b.id=${billId}


   and b.type = ${type} 


   and b.sums  >=  ${minSum}


   and b.sums  <  ${maxSum}


   and b.dates  >= ' ${startTime} '


   and b.dates  < ' ${endTime} ' 


   and b.sortId = ${sortId}


   and b.labelId = ${labelId}


   and (
   	b.descs like '%${condition}%' 
   	or bs.name like '%${condition}%' 
   	or bs.descs like '%${condition}%' 
   	or bl.name like '%${condition}%' 
   	or bl.descs like '%${condition}%'
	  )


   order by ${sortName}


    limit ${currentPage},${pageSize}

select t1.dates as dates, t2.type as type, sum(t3.sums) as sums from ( select DATE_FORMAT(dates,'${dateformat}') as dates from bill where userId=${userId}
and type = ${type}
and sums >= ${minSum}
and sums < ${maxSum}
and DATE_FORMAT(dates,'%Y-%m-%d') = DATE_FORMAT(now(),'%Y-%m-%d')
and DATE_FORMAT(dates,'%Y-%m-%d') = DATE_FORMAT(CURDATE()-1,'%Y-%m-%d')
and YEARWEEK(date_format(dates,'%Y-%m-%d'),1) = YEARWEEK(now())
and YEARWEEK(date_format(dates,'%Y-%m-%d'),1) = YEARWEEK(now())-1
and DATE_FORMAT(dates,'%Y-%m') = DATE_FORMAT(now(),'%Y-%m')
and DATE_FORMAT(dates,'%Y-%m') = DATE_FORMAT(DATE_SUB(curdate(), INTERVAL 1 MONTH),'%Y-%m')
and QUARTER(dates) = QUARTER(now())
and QUARTER(dates)=QUARTER(DATE_SUB(now(),interval 1 QUARTER))
and DATE_FORMAT(dates,'%Y') = DATE_FORMAT(now(),'%Y')
and DATE_FORMAT(dates,'%Y') = DATE_FORMAT(DATE_SUB(curdate(), INTERVAL 1 year),'%Y')
and dates >= ' ${startTime} '
and dates < ' ${endTime} '
and sortId = ${sortId}
and labelId = ${labelId}
group by DATE_FORMAT(dates,'${dateformat}') )t1 inner join ( select type from bill group by type ) t2 on 1=1 left join bill t3 on t1.dates=DATE_FORMAT(t3.dates,'${dateformat}') and t2.type=t3.type group by t1.dates, t2.type order by t1.dates

SELECT bs.id as id, bs.name as name,sum(b.sums) as value from bill b, billSort bs where b.sortId = bs.id and b.userId=${userId}
and b.type = ${type}
and b.sums >= ${minSum}
and b.sums < ${maxSum}
and b.dates >= ' ${startTime} '
and b.dates < ' ${endTime} '
and b.sortId = ${sortId}
and labelId = ${labelId}
and DATE_FORMAT(dates,'%Y-%m-%d') = DATE_FORMAT(now(),'%Y-%m-%d')
and DATE_FORMAT(dates,'%Y-%m-%d') = DATE_FORMAT(CURDATE()-1,'%Y-%m-%d')
and YEARWEEK(date_format(dates,'%Y-%m-%d'),1) = YEARWEEK(now())
and YEARWEEK(date_format(dates,'%Y-%m-%d'),1) = YEARWEEK(now())-1
and DATE_FORMAT(dates,'%Y-%m') = DATE_FORMAT(now(),'%Y-%m')
and DATE_FORMAT(dates,'%Y-%m') = DATE_FORMAT(DATE_SUB(curdate(), INTERVAL 1 MONTH),'%Y-%m')
and QUARTER(dates) = QUARTER(now())
and QUARTER(dates)=QUARTER(DATE_SUB(now(),interval 1 QUARTER))
and DATE_FORMAT(dates,'%Y') = DATE_FORMAT(now(),'%Y')
and DATE_FORMAT(dates,'%Y') = DATE_FORMAT(DATE_SUB(curdate(), INTERVAL 1 year),'%Y')
GROUP BY bs.id

SELECT bl.id as id, bl.name as name,sum(b.sums) as value from bill b, billLabel bl where b.labelId = bs.id and b.userId=${userId}
and b.type = ${type}
and b.sums >= ${minSum}
and b.sums < ${maxSum}
and b.dates >= ' ${startTime} '
and b.dates < ' ${endTime} '
and b.sortId = ${sortId}
and labelId = ${labelId}
and DATE_FORMAT(dates,'%Y-%m-%d') = DATE_FORMAT(now(),'%Y-%m-%d')
and DATE_FORMAT(dates,'%Y-%m-%d') = DATE_FORMAT(CURDATE()-1,'%Y-%m-%d')
and YEARWEEK(date_format(dates,'%Y-%m-%d'),1) = YEARWEEK(now())
and YEARWEEK(date_format(dates,'%Y-%m-%d'),1) = YEARWEEK(now())-1
and DATE_FORMAT(dates,'%Y-%m') = DATE_FORMAT(now(),'%Y-%m')
and DATE_FORMAT(dates,'%Y-%m') = DATE_FORMAT(DATE_SUB(curdate(), INTERVAL 1 MONTH),'%Y-%m')
and QUARTER(dates) = QUARTER(now())
and QUARTER(dates)=QUARTER(DATE_SUB(now(),interval 1 QUARTER))
and DATE_FORMAT(dates,'%Y') = DATE_FORMAT(now(),'%Y')
and DATE_FORMAT(dates,'%Y') = DATE_FORMAT(DATE_SUB(curdate(), INTERVAL 1 year),'%Y')
GROUP BY bl.id

SELECT case b.type when -1 then '支出' when 1 then '收入' else '未知' end as name,sum(b.sums) as value from bill b, billSort bs where b.sortId = bs.id and b.userId=${userId}
and b.type = ${type}
and b.sums >= ${minSum}
and b.sums < ${maxSum}
and b.dates >= ' ${startTime} '
and b.dates < ' ${endTime} '
and b.sortId = ${sortId}
and labelId = ${labelId}
and DATE_FORMAT(dates,'%Y-%m-%d') = DATE_FORMAT(now(),'%Y-%m-%d')
and DATE_FORMAT(dates,'%Y-%m-%d') = DATE_FORMAT(CURDATE()-1,'%Y-%m-%d')
and YEARWEEK(date_format(dates,'%Y-%m-%d'),1) = YEARWEEK(now())
and YEARWEEK(date_format(dates,'%Y-%m-%d'),1) = YEARWEEK(now())-1
and DATE_FORMAT(dates,'%Y-%m') = DATE_FORMAT(now(),'%Y-%m')
and DATE_FORMAT(dates,'%Y-%m') = DATE_FORMAT(DATE_SUB(curdate(), INTERVAL 1 MONTH),'%Y-%m')
and QUARTER(dates) = QUARTER(now())
and QUARTER(dates)=QUARTER(DATE_SUB(now(),interval 1 QUARTER))
and DATE_FORMAT(dates,'%Y') = DATE_FORMAT(now(),'%Y')
and DATE_FORMAT(dates,'%Y') = DATE_FORMAT(DATE_SUB(curdate(), INTERVAL 1 year),'%Y')
GROUP BY b.type
