mapper.select:
    select distinct count(*)
    from bill b, billMethod bm, billSort bs 
    where 
     b.methodId = bm.id and b.sortId = bs.id and b.userId=${userId}

      and b.id=${billId}




		      and b.sums  < -1* ${minSum} and b.sums  >=  ${minSum}


		      and b.sums  <  ${maxSum} and b.sums  >= -1* ${maxSum}




		      and b.sums  >=  ${minSum}


		      and b.sums  <  ${maxSum}




      and b.dates  >= ' ${startTime} '


      and b.dates  < ' ${endTime} ' 


      and b.methodId = ${methodId}


      and b.sortId = ${sortId}


      and (
      	b.descs like '%${condition}%' 
      	or bs.name like '%${condition}%' 
      	or bs.descs like '%${condition}%' 
      	or bm.name like '%${condition}%' 
      	or bm.descs like '%${condition}%'
   	  )

    select distinct 
    b.id as id, b.dates as dates, bm.id as methodId, bm.name as methodName,
    b.sums as sums, bs.id as sortId, bs.name as sortName 

     , b.descs as descs, b.createTime as createTime, b.updateTime as updateTime 

    from bill b, billMethod bm, billSort bs 
    where 
     b.methodId = bm.id and b.sortId = bs.id and b.userId=${userId}

      and b.id=${billId}


      and b.sums  >=  ${minSum}




		      and b.sums  < -1* ${minSum} and b.sums  >=  ${minSum}


		      and b.sums  <  ${maxSum} and b.sums  >= -1* ${maxSum}




		      and b.sums  >=  ${minSum}


		      and b.sums  <  ${maxSum}




      and b.dates  >= ' ${startTime} '


      and b.dates  < ' ${endTime} ' 


      and b.methodId = ${methodId}


      and b.sortId = ${sortId}


      and (
      	b.descs like '%${condition}%' 
      	or bs.name like '%${condition}%' 
      	or bs.descs like '%${condition}%' 
      	or bm.name like '%${condition}%' 
      	or bm.descs like '%${condition}%'
   	  )


      order by ${sortName}


       limit ${currentPage},${pageSize}
